  "blind": "ninguna persiana",
  "curtain": "ninguna cortina",
  "door": "ninguna puerta",
  "garage": "ninguna puerta de garaje",
  "gate": "ninguna puerta",
  "shade": "ningún estor",
  "shutter": "ninguna contraventana",
  "window": "ninguna ventana"
  } %}
